import React, {useState} from 'react'
import {View,Text, Button, StyleSheet} from 'react-native'
import Choice from "./Choice.js"

export default function MultipleChoice({ item, setAdvance, handleScroll }) {
    function checkAdvance(correct, buttonAnswer, setAdvance){
        if (correct == buttonAnswer){
            handleScroll(true)
        } else{
            handleScroll(false)
        }
    }
    return (
        <View style={styles.overall}>
            <Text style={styles.text}>{item.question}</Text>
            <View style={styles.container}>
                <View style={styles.column}>
                    <Choice title={item.multipleChoice.one} onPress={() => {checkAdvance(item.correctAnswer, item.multipleChoice.one, setAdvance)}} />
                    <Choice title={item.multipleChoice.two} onPress={() => {checkAdvance(item.correctAnswer, item.multipleChoice.two, setAdvance)}} />
                </View>
                <View style={styles.column}>
                    <Choice title={item.multipleChoice.three} onPress={() => {checkAdvance(item.correctAnswer, item.multipleChoice.three, setAdvance)}} />
                    <Choice title={item.multipleChoice.four} onPress={() => {checkAdvance(item.correctAnswer, item.multipleChoice.four, setAdvance)}} />
                </View>
            </View>
        </View>
    )
}


const styles = StyleSheet.create({
    text:{
        fontSize:20,
        color:'black',
        textAlign:'center',
        margin:5,
        fontWeight: 'bold',

    },
    button:{
        margin:5,
    },
    container:{
        flexDirection:'row',
        flex:1,
        justifyContent:"center"
    },
    overall:{
        width:"95%",
        height:"25%",
        borderRadius:10
    }
  });
